  warn(message: string): void {
    if (this.shouldLog(LogLevel.WARNING)) {
      console.warn(`[Game Warning] ${message}`);
    }
  }

  error(message: string): void {
    if (this.shouldLog(LogLevel.ERROR)) {
      console.error(`[Game Error] ${message}`);
    }
  }
}
