```typescript
export default class Pathfinding implements IPathfinding {
  private grid: Grid;

  constructor(grid: Grid) {
    this.grid = grid;
  }

  findPath(start: GridNode, end: GridNode): PathfindingResult {
    const openSet: GridNode[] = [start];
    const closedSet: GridNode[] = [];

    while (openSet.length > 0) {
      const currentNode = openSet[0];
      openSet.splice(0, 1);
      closedSet.push(currentNode);

      if (currentNode === end) {
        return { success: true, path: this.reconstructPath(start, end) };
      }

      const neighbors = this.grid.getNeighbors(currentNode);

      for (const neighbor of neighbors) {
        if (closedSet.includes(neighbor) || !neighbor.walkable) {
          continue;
        }

        openSet.push(neighbor);
      }
    }

    return { success: false, path: null };
  }

  private reconstructPath(start: GridNode, end: GridNode): GridNode[] {
    const path: GridNode[] = [];
    let currentNode = end;

    while (currentNode !== start) {
      path.unshift(currentNode);
      currentNode = currentNode.parent!;
    }

    path.unshift(start);
    return path;
  }
}
